package basicPrograms;

public class PrintEvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Write a program to print even numbers from 1 to 100 range
		
		for(int i=1; i<=100; i++) {
			
			if(i%2 == 0) {
			   System.out.println(i );
			}
		}

	}

}
